import { createBoard } from '@wixc3/react-board';
import { Image } from 'semantic-ui-react';

// board cover
import cover from './image-group.png';

const src = 'https://react.semantic-ui.com/images/wireframe/image.png';

export default createBoard({
    name: 'Image Group',
    Board: () => (
        <Image.Group size="small">
            <Image src={src} />
            <Image src={src} />
            <Image src={src} />
            <Image src={src} />
        </Image.Group>
    ),
    tags: ['Image', 'Group'],
    cover,
    environmentProps: {
        canvasPadding: { top: 18, bottom: 10, right: 10, left: 10 },
        windowBackgroundColor: '#fcfcff',
    },
});
