// board cover
import cover from './vertically-aligned.png';

const src = 'https://react.semantic-ui.com/images/wireframe/image.png';

export default createBoard({
    name: 'Vertically Aligned',
    Board: () => (
        <div>
            <Image src={src} size="tiny" verticalAlign="top" /> <span>Top Aligned</span>
            <Divider />
            <Image src={src} size="tiny" verticalAlign="middle" /> <span>Middle Aligned</span>
            <Divider />
            <Image src={src} size="tiny" verticalAlign="bottom" /> <span>Bottom Aligned</span>
        </div>
    ),
    tags: ['Divider', 'Image', 'Vertically', 'Aligned'],
    cover,
    environmentProps: {
        canvasPadding: { top: 10, bottom: 10, right: 10, left: 10 },
        windowBackgroundColor: '#fcfcff',
    },
});
